                                                                                                        <div
                                                                                                            class="textable">
                                                                                                            <p>1.
                                                                                                                Продавец
                                                                                                                гарантирует,
                                                                                                                что
                                                                                                                купленное
                                                                                                                изделие
                                                                                                                является
                                                                                                                работоспособным
                                                                                                                и не
                                                                                                                содержит
                                                                                                                выявленных
                                                                                                                механических
                                                                                                                и иных
                                                                                                                повреждений
                                                                                                                на
                                                                                                                момент
                                                                                                                осуществления
                                                                                                                продажи.
                                                                                                                Комплектность
                                                                                                                изделий
                                                                                                                проверяется
                                                                                                                при
                                                                                                                покупке.
                                                                                                            </p>
                                                                                                            <p
                                                                                                                align="left">
                                                                                                                2.
                                                                                                                Гарантия
                                                                                                                на
                                                                                                                проданные
                                                                                                                изделия,
                                                                                                                при
                                                                                                                условии
                                                                                                                соблюдения
                                                                                                                условий
                                                                                                                гарантии,
                                                                                                                подразумевает
                                                                                                                под
                                                                                                                собой
                                                                                                                бесплатный
                                                                                                                ремонт
                                                                                                                изделий
                                                                                                                в
                                                                                                                течение
                                                                                                                гарантийного
                                                                                                                срока,
                                                                                                                указанного
                                                                                                                в
                                                                                                                гарантийном
                                                                                                                талоне,
                                                                                                                либо их
                                                                                                                замену
                                                                                                                на
                                                                                                                аналогичные
                                                                                                                по
                                                                                                                назначению
                                                                                                                в случае
                                                                                                                невозможности
                                                                                                                ремонта.
                                                                                                            </p>
                                                                                                            <p
                                                                                                                align="left">
                                                                                                                3.
                                                                                                                Гарантия
                                                                                                                действительна
                                                                                                                только
                                                                                                                при
                                                                                                                наличии
                                                                                                                Гарантийного
                                                                                                                талона,
                                                                                                                подписанного
                                                                                                                Покупателем.
                                                                                                            </p>
                                                                                                            <p
                                                                                                                align="left">
                                                                                                                4.
                                                                                                                Покупатель
                                                                                                                обязан
                                                                                                                до
                                                                                                                начала
                                                                                                                ввода в
                                                                                                                эксплуатацию
                                                                                                                оборудования
                                                                                                                ознакомиться
                                                                                                                с
                                                                                                                данными
                                                                                                                правилами
                                                                                                                гарантийного
                                                                                                                обслуживания,
                                                                                                                и
                                                                                                                убедиться
                                                                                                                в
                                                                                                                отсутствии
                                                                                                                внешних
                                                                                                                повреждений.
                                                                                                            </p>
                                                                                                            <p
                                                                                                                align="left">
                                                                                                                5.
                                                                                                                Продавец
                                                                                                                не
                                                                                                                обеспечивает
                                                                                                                гарантийное
                                                                                                                обслуживание
                                                                                                                поставленных
                                                                                                                изделий
                                                                                                                в
                                                                                                                случаях:
                                                                                                            </p>
                                                                                                            <p
                                                                                                                align="left">
                                                                                                                - утери
                                                                                                                гарантийного
                                                                                                                талона,
                                                                                                                наличия
                                                                                                                в нём
                                                                                                                записей
                                                                                                                или
                                                                                                                исправлений,
                                                                                                                сделанных
                                                                                                                не
                                                                                                                Продавцом(кроме
                                                                                                                полей
                                                                                                                подлежащих
                                                                                                                заполнению
                                                                                                                Покупателем);
                                                                                                            </p>
                                                                                                            <p
                                                                                                                align="left">
                                                                                                                -
                                                                                                                наличия
                                                                                                                механических
                                                                                                                повреждений
                                                                                                                (помятости,
                                                                                                                сколы,
                                                                                                                глубокие
                                                                                                                царапины,
                                                                                                                трещины,
                                                                                                                следы
                                                                                                                удара,
                                                                                                                изгиб
                                                                                                                или
                                                                                                                перелом
                                                                                                                товара,
                                                                                                                и т.п.),
                                                                                                                а также
                                                                                                                со
                                                                                                                стёртой
                                                                                                                маркировкой
                                                                                                                изделий,
                                                                                                                неразборчивой
                                                                                                                маркировкой;
                                                                                                            </p>
                                                                                                            <p
                                                                                                                align="left">
                                                                                                                -
                                                                                                                наличия
                                                                                                                признаков
                                                                                                                ремонта
                                                                                                                неуполномоченными
                                                                                                                лицами
                                                                                                                (вскрытия
                                                                                                                блоков,
                                                                                                                следов
                                                                                                                пайки,
                                                                                                                следов
                                                                                                                механических
                                                                                                                повреждений
                                                                                                                на
                                                                                                                винтах,
                                                                                                                нарушения
                                                                                                                гарантийных
                                                                                                                пломб,
                                                                                                                замены
                                                                                                                деталей
                                                                                                                и т.п.);
                                                                                                            </p>
                                                                                                            <p
                                                                                                                align="left">
                                                                                                                -
                                                                                                                наличия
                                                                                                                повреждений,
                                                                                                                полученных
                                                                                                                в
                                                                                                                результате
                                                                                                                аварий,
                                                                                                                воздействия
                                                                                                                огня,
                                                                                                                влаги,
