    <script>
        $(function () {
            if ($(window).scrollTop() >= 250) $(".goup").fadeIn("slow")
            $(window).scroll(function () {
                if ($(window).scrollTop() <= 550) $(".goup").fadeOut("slow")
                else $(".goup").fadeIn("slow")
            });
            $(".goup").click(function () {
                $("html,body").animate({
                    scrollTop: 0
                }, "slow");
            });
        });
        $(".comment__show").click(function () {
            $(this).remove();
            $(".comment2__wrapper").addClass("active");
        });
        $(".pagination__item").click(function () {
            $(this).addClass("active").siblings().removeClass("active");
            var thisId = $(this).attr("data-id");
            $(".comment2__box_" + thisId).addClass("active").siblings(".comment2__box").removeClass("active");
        });
        $("#uid37, #uid52, #uid67, #uid148, #uid159, #uid170").click(function (event) {
            event.preventDefault();
            var thisTitle = $(this).attr("data-title");
            $(".formname").val(thisTitle);
            var dataId = $(this).attr("data-id");
            var modalH2 = $(".modal-cont-" + dataId).height();
            $(".modal-cont-" + dataId).css({
                'marginTop': -modalH2 / 2
            });
            $(".overlay").fadeIn(600, function () {
                $(".modal-cont-" + dataId)
                    .css('z-index', '9999')
                    .animate({
                        opacity: 1,
                        top: "45%"
                    }, 200)
            });
        });
        $('.modal-close,.overlay').click(function () {
            $(".modal-cont").animate({
                    opacity: 0,
                    top: "45%"
                }, 200,
                function () {
                    $(this).css('z-index', '-1');
                    $('.overlay').fadeOut(400);
                }
            );
        });
    </script>
