window.addEventListener("DOMContentLoaded",()=>{const menu=document.querySelector(".menu"),about=document.querySelector(".about");let menuPositionFirst=menu.getBoundingClientRect().top,aboutPosition=about.getBoundingClientRect().top,sectionHeight=menu.clientHeight;menuPositionFirst<=0&&menu.classList.add("menu_sticky"),aboutPosition>=0+sectionHeight&&menu.classList.remove("menu_sticky"),window.addEventListener("scroll",(function(){let menuPositionScroll=menu.getBoundingClientRect().top,aboutPosition=about.getBoundingClientRect().top,sectionHeight=menu.clientHeight;menuPositionScroll<=0&&menu.classList.add("menu_sticky"),aboutPosition>=0+sectionHeight&&menu.classList.remove("menu_sticky")}));const sections=document.querySelectorAll("[data-change-menu-link-color]");window.addEventListener("scroll",(function(){for(let section of sections){const sectionID=section.dataset.changeMenuLinkColor;let sectionPosition=document.querySelector(sectionID).getBoundingClientRect().top,sectionHeight=document.querySelector(sectionID).clientHeight,menuItemHrefSectionID=document.querySelector(`.menu__link[href="${sectionID}"]`);sectionPosition>=0-sectionHeight/2&&sectionPosition<=sectionHeight?menuItemHrefSectionID.classList.add("menu__link_active"):menuItemHrefSectionID.classList.remove("menu__link_active")}}));const menuLink=document.querySelectorAll(".menu__link"),hamburger=document.querySelector(".hamburger");hamburger.addEventListener("click",()=>{hamburger.classList.toggle("hamburger_active"),menu.classList.toggle("menu_active")}),menuLink.forEach(item=>{item.addEventListener("click",e=>{e.preventDefault(),hamburger.classList.toggle("hamburger_active"),menu.classList.toggle("menu_active");let blockID=item.getAttribute("href");document.querySelector(blockID).scrollIntoView({behavior:"smooth",block:"start"})})});const localLinks=document.querySelectorAll('[data-scroll="smooth"]');for(let localLink of localLinks)localLink.addEventListener("click",(function(e){e.preventDefault();let blockID=localLink.getAttribute("href");document.querySelector(blockID).scrollIntoView({behavior:"smooth",block:"start"})}))});